INSERT INTO t_p18501900_new_website_project.predmety_v_smole (name, description, price, image_url) VALUES
('Награда ЕНИСЕЙ 15 лет', 'Прозрачная стела с желтым гидравлическим молотом Delta Presis в смоле. Награда выполнена в виде трехгранной призмы с изображением строительной техники. Основание из текстурированного бетона с логотипом Группы компаний Традиция. Идеально подходит для награждения в строительной сфере.', 15000, 'https://cdn.poehali.dev/files/6ee6db3a-462b-45e3-85ce-0bdfac3457b8.JPG'),
('Награда Динамика роста 2022', 'Элегантная награда в виде синего хрустального пламени на черном постаменте. Текст "Динамика роста 2022" выгравирован на основании. Символизирует развитие и достижение новых высот. Выполнена из высококачественного синего акрила с эффектом льда.', 12000, 'https://cdn.poehali.dev/files/38b999d6-01f7-4c7b-9557-7dc4accaac97.JPG'),
('Награда Детский Питеркит 2022', 'Стеклянная стела с акриловым китом в градиенте фиолетового и бирюзового цветов. На белом основании логотип Детского Питеркита и даты проведения 30/10/2022-3/11/2022. Креативный дизайн идеально подходит для детских мероприятий и фестивалей.', 13500, 'https://cdn.poehali.dev/files/92286a07-a657-47bc-a758-70632f605409.JPG'),
('Часы авиационные в смоле', 'Уникальные часы из эпоксидной смолы с натуральным деревом и голубой заливкой. Встроен настоящий авиационный альтиметр. Комбинация дерева и бирюзовой смолы создает эффект реки. Отличный подарок для авиаторов и любителей авиации.', 18000, 'https://cdn.poehali.dev/files/597e8877-acda-489a-a0f9-c79eebdbabb9.JPG'),
('Награда Кубок ректора АГУ 2023', 'Стеклянный куб на матовой подставке с логотипом АГУ в голубом цвете. Лаконичный и престижный дизайн. Текст "Кубок ректора АГУ 2023" выгравирован на основании. Идеально подходит для академических наград и университетских достижений.', 11000, 'https://cdn.poehali.dev/files/d8ef7d6b-5844-4e17-a0d7-652c03270791.JPG'),
('Награда Строитель года 2022', 'Золотистая награда с прозрачным акриловым элементом в форме буквы Q. Текст "СТРОИТЕЛЬ ГОДА 2022" на золотом кубическом основании. Логотип с треугольником и черной стрелкой. Премиальный дизайн для строительной индустрии.', 16000, 'https://cdn.poehali.dev/files/ca7a6120-47e8-41b1-a11d-36be1b910d88.JPG'),
('Награда Капля воды с пейзажем', 'Прозрачная призма с инкапсулированной голубой каплей воды и золотистым горным ландшафтом. На основании круглая эмблема "Капельная магистраль" с изображением ГЭП Водоканал Санкт-Петербурга. Даты: 10-й юбилейный конкурс среди аэротенков. Уникальная награда для водоканала.', 14500, 'https://cdn.poehali.dev/files/71ae2802-dbf1-4ba1-abb0-0c0645f87e09.JPG'),
('Награда Ice Cream 25 лет', 'Акриловая награда в форме мороженого на прозрачной подставке. Золотая надпись "Ice Cream Dance Company" и значок "25". Текст "BEST OF THE BEST 2022" на основании. Креативный дизайн для творческих коллективов и танцевальных студий.', 10500, 'https://cdn.poehali.dev/files/54b2a01a-4b4b-4943-813e-df27220c240d.JPG'),
('Награда Жест победы', 'Прозрачная акриловая скульптура руки с поднятым указательным пальцем на матовой подставке. Символ победы, лидерства и достижения цели. Минималистичный и современный дизайн. Универсальная награда для любых достижений.', 9500, 'https://cdn.poehali.dev/files/e3776c3d-1f44-4549-a0ae-e106fcf396cf.JPG'),
('Герб на красно-синем фоне', 'Деревянная награда с эпоксидной смолой синего цвета, имитирующей волны океана. Красный фон сверху. В центре деревянная плашка с гербом в виде лодки и двух арок. Сочетание натурального дерева, красного и синего цветов создает морскую тематику.', 13000, 'https://cdn.poehali.dev/files/91b49762-2076-4814-a52c-cde5af4511d3.JPG'),
('Черная кошка в янтарном кубе', 'Прозрачный куб с инкапсулированной бронзовой фигуркой черной кошки. Янтарный оттенок смолы создает теплое свечение. Кошка застыла в игривой позе. Оригинальный подарок для любителей кошек и необычных сувениров.', 8500, 'https://cdn.poehali.dev/files/a78d2c2e-724e-4026-b379-8bfa06dae4e8.JPG');